"use strict";

var curry = require("../Strict/curry.js");

var mapL = require("../Lazy/mapL.js");

var takeUntilL = require("../Lazy/takeUntilL.js");

var identity = require("../Strict/identity.js");

var reduceC = require("./reduceC.js");

var go = require("../Strict/go.js");

module.exports = curry(function someC(f, iter) {
  return go(mapL(f, iter), takeUntilL(identity), reduceC((a, b) => a || b), (a = false) => a, Boolean);
});