"use strict";

require("core-js/modules/es.array.from");

var curry2 = require("./curry2.js");

var takeAll = require("./takeAll.js");

var updateByL = require("../Lazy/updateByL.js");

module.exports = curry2(function updateBy(index, f, iter) {
  if (index < 0) {
    const arr = Array.from(iter);
    return updateBy(arr.length + index, f, arr);
  } else {
    return takeAll(updateByL(index, f, iter));
  }
});